mapper.update:
UPDATE menu SET men_order = #{menuBean.men_order}
WHERE men_order =

        #{menuBean.men_order}-1
        #{menuBean.men_order}+1


       AND men_parent = #{menuBean.men_parent}

UPDATE menu SET men_order = #{menuBean.men_order}

                                            -1
                                            +1

WHERE men_id = #{menuBean.men_id}
